// src/components/DeviceForm.js

import { useEffect, useState } from 'react';

const DeviceForm = ({ initialValues, onSubmit, onCancel }) => {
  const [device, setDevice] = useState({ name: '', status: '' });

  useEffect(() => {
    if (initialValues) {
      setDevice(initialValues);
    } else {
      setDevice({ name: '', status: '' });
    }
  }, [initialValues]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDevice(prev => ({ ...prev, [name]: value }));
  };

const [loading, setLoading] = useState(false);
const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  try {
    await onSubmit(device);
    setDevice({ name: '', status: '' });
  } catch (error) {
    console.error('Submission failed:', error);
  } finally {
    setLoading(false);
  }
};

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '1rem' }}>
      <input
        type="text"
        name="name"
        placeholder="Device Name"
        value={device.name}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="status"
        placeholder="Status"
        value={device.status}
        onChange={handleChange}
        required
      />
      <button type="submit">{initialValues ? 'Update' : 'Add'} Device</button>
      {onCancel && (
        <button type="button" onClick={onCancel} style={{ marginLeft: '8px' }}>
          Cancel
        </button>
      )}
    </form>
  );
};

export default DeviceForm;
