// src/components/DeviceList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DeviceList = () => {
  const [devices, setDevices] = useState([]);
  const [newDevice, setNewDevice] = useState({ name: '', status: '' });
  const [editingDevice, setEditingDevice] = useState(null);

  useEffect(() => {
    fetchDevices();
  }, []);

  const fetchDevices = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/devices');
      setDevices(response.data);
    } catch (error) {
      console.error('Error fetching devices:', error);
    }
  };

  const handleAddDevice = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/devices', newDevice);
      setDevices([...devices, response.data]);
      setNewDevice({ name: '', status: '' });
    } catch (error) {
      console.error('Error adding device:', error);
    }
  };

  const handleDeleteDevice = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/devices/${id}`);
      setDevices(devices.filter(device => device.id !== id));
    } catch (error) {
      console.error('Error deleting device:', error);
    }
  };

  const startEditDevice = (device) => {
    setEditingDevice(device);
  };

  const handleUpdateDevice = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:8080/api/devices/${editingDevice.id}`, editingDevice);
      setDevices(devices.map(d => d.id === editingDevice.id ? response.data : d));
      setEditingDevice(null);
    } catch (error) {
      console.error('Error updating device:', error);
    }
  };

  return (
    <div>
      <h2>Device List</h2>
      <form onSubmit={handleAddDevice}>
        <input
          type="text"
          placeholder="Device Name"
          value={newDevice.name}
          onChange={e => setNewDevice({ ...newDevice, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Status"
          value={newDevice.status}
          onChange={e => setNewDevice({ ...newDevice, status: e.target.value })}
          required
        />
        <button type="submit">Add Device</button>
      </form>

      {editingDevice && (
        <form onSubmit={handleUpdateDevice}>
          <h3>Edit Device</h3>
          <input
            type="text"
            value={editingDevice.name}
            onChange={e => setEditingDevice({ ...editingDevice, name: e.target.value })}
          />
          <input
            type="text"
            value={editingDevice.status}
            onChange={e => setEditingDevice({ ...editingDevice, status: e.target.value })}
          />
          <button type="submit">Update</button>
          <button type="button" onClick={() => setEditingDevice(null)}>Cancel</button>
        </form>
      )}

      <ul>
        {devices.map(device => (
          <li key={device.id}>
            {device.name} - {device.status}
            <button onClick={() => startEditDevice(device)}>Edit</button>
            <button onClick={() => handleDeleteDevice(device.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DeviceList;
