// src/components/DeviceList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import DeviceForm from './DeviceForm';

const authHeader = 'Basic ' + btoa('admin:admin123');

const DeviceList = () => {
  const [devices, setDevices] = useState([]);
  const [editingDevice, setEditingDevice] = useState(null);

  const fetchDevices = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/devices');
      setDevices(response.data);
    } catch (error) {
      console.error('Error fetching devices:', error);
    }
  };

  useEffect(() => {
    fetchDevices();
  }, []);

  const handleAddDevice = async (device) => {
    try {
      const response = await axios.post('http://localhost:8080/api/devices', device, {
  headers: {
    'Authorization': 'Basic ' + btoa('admin:admin123'),
  },
});
      setDevices([...devices, response.data]);
    } catch (error) {
      console.error('Error adding device:', error);
    }
  };

  const handleUpdateDevice = async (device) => {
    try {
      const response = await axios.put(`http://localhost:8080/api/devices/${device.id}`, device);
      setDevices(devices.map(d => (d.id === device.id ? response.data : d)));
      setEditingDevice(null);
    } catch (error) {
      console.error('Error updating device:', error);
    }
  };

  const handleDeleteDevice = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/devices/${id}`);
      setDevices(devices.filter(d => d.id !== id));
    } catch (error) {
      console.error('Error deleting device:', error);
    }
  };

  return (
    <div>
      <h2>Device List</h2>

      <DeviceForm
        initialValues={null}
        onSubmit={handleAddDevice}
      />

      {editingDevice && (
        <DeviceForm
          initialValues={editingDevice}
          onSubmit={handleUpdateDevice}
          onCancel={() => setEditingDevice(null)}
        />
      )}

      <ul>
        {devices.map(device => (
          <li key={device.id}>
            <strong>{device.name}</strong> - {device.status}
            <button onClick={() => setEditingDevice(device)}>Edit</button>
            <button onClick={() => handleDeleteDevice(device.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DeviceList;
